import React, { useState } from "react";

const App = () => {
  const purple = "purple";

  const [bg, setbg] = useState(purple);
  const [name,setname]=useState('Click Me');

  const bgchange = () => {
    const newbg='green';
    setbg(newbg);
    setname('Ouchh ðŸ˜±'); 
  };
  const bgb=()=>{
    setbg(purple);
    setname('Ayooo');
  }

  return (
    <>
      <div style={{ backgroundColor: bg }}>
        <button onClick={bgchange} onDoubleClick={bgb}>{name}</button>
      </div>
    </>
  );
};
export default App;
