import React, { useState } from "react";

const App=()=>{
  const [name,setname]=useState();

  const incre=()=>{
    setname(name=1)
  }
  return(
    <>
    <div className='main_div'>
    <div className='centre_div'>
    <h1>0</h1>
    <button onClick={incre}>Increment</button>
    <button>Decrement</button>
    </div>
    </div>
    </>

  )
}
export default App;
