import React, { useState } from "react";
import Createnote from "./createnote";
import Footer from "./Footer";
import Header from "./Header";
import Note from "./Note";

const App = () => {
  const [adddata,setadddata]=useState([]);
  const callevent = note => {
    //  alert('i am clicked!')
    setadddata((preve)=>{
      return[...preve,note];
    })
  };
  const ondelete=(id)=>{
    setadddata((olddata)=>
    olddata.filter((curdata,inx)=>{
      return inx!=id;
    })
    )

  }
  return (
    <>
      <Header />
      <Createnote  passnote={callevent} />
      
      {adddata.map((val,index)=>{
        return (
          <Note 
            key={index}
            id={index}
            title={val.title}
            content={val.content}
            deleteitem={ondelete}
          />
        )
      })}
      <Footer />
    </>
  );
};
export default App;
