import React, { useState } from "react";
import Todolist from "./Todolist";

const App = () => {
  const [additem, setadditem] = useState("");
  const [item, setitem] = useState([]);

  const changeevent = event => {
    setadditem(event.target.value);
  };
  const submit = () => {
    setitem(setvalue => {
      return [...setvalue, additem];
    });
    setadditem("");
  };
  const deleteitem = (id) => {
    console.log("deleted");

    setitem((setvalue)=>{
        return setvalue.filter(()=>{
              return
        })
    })
  };
  return (
    <>
      <div className="main_div">
        <div className="centre_div">
          <br />
          <h1>ToDo List</h1>
          <br />
          <input
            type="text"
            placeholder="Add a Items"
            onChange={changeevent}
            value={additem}
          />
          <button onClick={submit}>+</button>

          <ol>
            {/* <li>{additem}</li> */}
            {item.map((ivalue, index) => {
              return (
                <Todolist
                  key={index}
                  id={index}
                  text={ivalue}
                  onselect={deleteitem}
                />
              );
            })}
          </ol>
        </div>
      </div>
    </>
  );
};
export default App;
